import React, { useState, useEffect } from 'react';
import EmblemIcon from './EmblemIcon';

const MobileMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  // Prevent body scroll when menu is open and animate menu items
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
      // Animate menu items in
      setTimeout(() => {
        const menuItems = document.querySelectorAll('.mobile-menu-item');
        menuItems.forEach((item, index) => {
          setTimeout(() => {
            item.style.opacity = '1';
            item.style.transform = 'translateX(0)';
          }, index * 100);
        });
      }, 200);
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen]);

  return (
    <div className="md:hidden">
      {/* Hamburger Button - Super Sleek */}
      <button
        onClick={toggleMenu}
        className="relative p-3 rounded-xl bg-gradient-to-r from-primary-50 to-accent-50 hover:from-primary-100 hover:to-accent-100 transition-all duration-300 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-primary-400 focus:ring-opacity-50 shadow-sm"
        aria-label="Toggle menu"
      >
        <div className="w-6 h-6 flex flex-col justify-center items-center">
          <span className={`block h-0.5 w-6 bg-gradient-to-r from-primary-600 to-accent-600 rounded-full transition-all duration-300 ${isOpen ? 'rotate-45 translate-y-1.5' : ''}`}></span>
          <span className={`block h-0.5 w-6 bg-gradient-to-r from-primary-600 to-accent-600 rounded-full transition-all duration-300 mt-1.5 ${isOpen ? 'opacity-0' : ''}`}></span>
          <span className={`block h-0.5 w-6 bg-gradient-to-r from-primary-600 to-accent-600 rounded-full transition-all duration-300 mt-1.5 ${isOpen ? '-rotate-45 -translate-y-1.5' : ''}`}></span>
        </div>
      </button>

      {/* Mobile Menu Overlay - Gorgeous Design */}
      {isOpen && (
        <>
          {/* Backdrop with blur effect */}
          <div 
            className="fixed inset-0 z-40 bg-black/20 backdrop-blur-sm transition-opacity duration-300" 
            onClick={toggleMenu}
          />
          
          {/* Menu Panel - Absolutely Stunning */}
          <div 
            className={`fixed top-0 right-0 h-full w-80 max-w-[85vw] z-50 transform transition-all duration-500 ease-out ${
              isOpen ? 'translate-x-0' : 'translate-x-full'
            }`}
            onClick={(e) => e.stopPropagation()}
          >
            {/* Glass morphism background */}
            <div className="h-full bg-white/95 backdrop-blur-xl border-l border-white/20 shadow-2xl">
              
              {/* Header with gradient and emblem */}
              <div className="relative bg-gradient-to-br from-primary-600 via-primary-700 to-accent-600 p-6 pb-8">
                {/* Background pattern */}
                <div className="absolute inset-0 opacity-10">
                  <div className="absolute top-4 right-4">
                    <EmblemIcon className="w-16 h-16 text-white/30" />
                  </div>
                  <div className="absolute bottom-2 left-4">
                    <EmblemIcon className="w-8 h-8 text-white/20" />
                  </div>
                </div>
                
                {/* Header content */}
                <div className="relative flex items-center justify-between">
                  <div>
                    <h2 className="text-white font-bold text-xl font-poppins">Karvaan</h2>
                    <p className="text-white/80 text-sm mt-1">Travel Platform</p>
                  </div>
                  <button
                    onClick={toggleMenu}
                    className="p-2 rounded-full bg-white/20 hover:bg-white/30 transition-all duration-200 backdrop-blur-sm"
                  >
                    <svg className="h-5 w-5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
              </div>

              {/* Navigation Links - Elegant spacing */}
              <nav className="px-6 py-8">
                <div className="space-y-3">
                  {[
                    { href: "#features", label: "Features", icon: "âœ¨", desc: "Explore our platform" },
                    { href: "#how-it-works", label: "How It Works", icon: "ðŸš€", desc: "Simple 3-step process" },
                    { href: "#benefits", label: "Benefits", icon: "ðŸ’Ž", desc: "Transform your business" }
                  ].map((item, index) => (
                    <a
                      key={item.href}
                      href={item.href}
                      className="mobile-menu-item group flex items-center space-x-4 p-4 rounded-2xl hover:bg-gradient-to-r hover:from-primary-50 hover:to-accent-50 transition-all duration-300 transform hover:scale-[1.02] hover:shadow-lg nav-link-hover"
                      onClick={toggleMenu}
                      style={{ 
                        animationDelay: `${index * 150}ms`,
                        opacity: 0,
                        transform: 'translateX(50px)'
                      }}
                    >
                      <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-primary-100 to-accent-100 flex items-center justify-center group-hover:from-primary-200 group-hover:to-accent-200 transition-all duration-300 group-hover:scale-110 shadow-sm">
                        <span className="text-xl group-hover:scale-110 transition-transform duration-200">{item.icon}</span>
                      </div>
                      <div className="flex-1">
                        <div className="text-gray-800 font-semibold text-lg group-hover:text-primary-700 transition-colors duration-200">
                          {item.label}
                        </div>
                        <div className="text-gray-500 text-sm group-hover:text-primary-500 transition-colors duration-200">
                          {item.desc}
                        </div>
                      </div>
                      <div className="opacity-0 group-hover:opacity-100 transition-all duration-200 transform group-hover:translate-x-1">
                        <svg className="w-5 h-5 text-primary-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                      </div>
                    </a>
                  ))}
                </div>

                {/* CTA Buttons - Gorgeous design */}
                <div className="mt-8 space-y-4">
                  <a
                    href="https://wa.me/923335215568?text=Hi, I want to request a demo of Karvaan platform"
                    className="block w-full bg-gradient-to-r from-primary-600 to-accent-600 text-white px-6 py-4 rounded-2xl font-semibold text-center hover:from-primary-700 hover:to-accent-700 transition-all duration-300 transform hover:scale-[1.02] shadow-lg hover:shadow-xl"
                    onClick={toggleMenu}
                  >
                    <div className="flex items-center justify-center space-x-2">
                      <span>Request Demo</span>
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                      </svg>
                    </div>
                  </a>
                  
                  <a
                    href="https://wa.me/923335215568?text=Hi, I'd like to know more about Karvaan features"
                    className="block w-full border-2 border-gradient-to-r from-primary-200 to-accent-200 bg-gradient-to-r from-primary-50 to-accent-50 text-primary-700 px-6 py-4 rounded-2xl font-semibold text-center hover:from-primary-100 hover:to-accent-100 transition-all duration-300 transform hover:scale-[1.02]"
                    onClick={toggleMenu}
                  >
                    <div className="flex items-center justify-center space-x-2">
                      <span>Get Started</span>
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
