import React, { useState } from 'react';

const MobileMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="md:hidden">
      <button
        onClick={toggleMenu}
        className="p-2 rounded-md text-gray-600 hover:text-gray-900 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-primary-500"
        aria-label="Toggle menu"
      >
        <svg
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          {isOpen ? (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M6 18L18 6M6 6l12 12"
            />
          ) : (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 6h16M4 12h16M4 18h16"
            />
          )}
        </svg>
      </button>

      {/* Mobile menu overlay */}
      {isOpen && (
        <div className="fixed inset-0 z-50 bg-black bg-opacity-50" onClick={toggleMenu}>
          <div 
            className="fixed top-0 right-0 h-full w-64 bg-white shadow-lg transform transition-transform duration-300 ease-in-out"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="flex items-center justify-between p-4 border-b">
              <span className="text-lg font-semibold text-gray-900">Menu</span>
              <button
                onClick={toggleMenu}
                className="p-2 rounded-md text-gray-600 hover:text-gray-900 hover:bg-gray-100"
              >
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <nav className="p-4">
              <div className="space-y-4">
                <a
                  href="#features"
                  className="block py-2 text-gray-600 hover:text-gray-900 transition-colors"
                  onClick={toggleMenu}
                >
                  Features
                </a>
                <a
                  href="#how-it-works"
                  className="block py-2 text-gray-600 hover:text-gray-900 transition-colors"
                  onClick={toggleMenu}
                >
                  How It Works
                </a>
                <a
                  href="#benefits"
                  className="block py-2 text-gray-600 hover:text-gray-900 transition-colors"
                  onClick={toggleMenu}
                >
                  Benefits
                </a>
                <a
                  href="https://wa.me/923335215568?text=Hi, I want to request a demo of Karvaan platform"
                  className="block w-full mt-4 bg-gradient-to-r from-primary-600 to-accent-600 text-white px-6 py-3 rounded-lg hover:from-primary-700 hover:to-accent-700 transition-all duration-300 text-center"
                  onClick={toggleMenu}
                >
                  Request Demo
                </a>
              </div>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default MobileMenu;
